.direction     = CCScrollViewDirectionHorizontal;                [sclView addChild:pic];        [self addChild:sclView];                pic     = [CCSprite spriteWithFile:@"pic.jpg"];        sclView = [CCScrollView scrollViewWithViewSize:CGSizeMake(winSize.width*0.5, winSize.height*0.5)];        pic.position          = ccp(0.0f, 0.0f);        sclView.position      = ccp(sclView.viewSize.width, sclView.viewSize.height);        sclView.contentOffset = ccp(0.0f, 0.0f);        sclView.contentSize   = pic.contentSize;        sclView.bounces       = NO;                [sclView addChild:pic];        [self addChild:sclView];                [pool drain];    }    return self;}@end